pragma solidity ^0.4.24;

contract MediaGallery {
    address owner;
    uint counter;
    bool stop;
    address[] public contributors; 

    struct MediaAsset {
        uint id;
        string name;
        string description;
        address author;
        uint createDate;
        string tags;
        string mediaHash;
    }

    mapping(address => MediaAsset[]) public mediaDatabase;

    event LogNewMediaAsset(
        uint indexed _id,
        string _name,
        address _author,
        uint _createDate,
        string _mediaHash
    );

    constructor () {
        owner = msg.sender;
    }

    function addMedia(
        string _name,
        string _description,
        string _mediaHash,
        string _tags
        ) public returns (bool success) {
        MediaAsset memory currentMedia;
        uint date = block.timestamp;

        currentMedia.id = counter;
        currentMedia.name = _name;
        currentMedia.description = _description;
        currentMedia.author = msg.sender;
        currentMedia.createDate = date;
        currentMedia.mediaHash = _mediaHash;
        currentMedia.tags = _tags;

        mediaDatabase[msg.sender].push(currentMedia);

        emit LogNewMediaAsset(
            counter,
            _name,
            msg.sender,
            date,
            _mediaHash
        );

        contributors.push(msg.sender);

        counter++;

        return true;
    }

    function getMediaByAddress(address _user) public view returns (uint[]) {
        uint[] memory mediaAssetIds = new uint[](mediaDatabase[_user].length);

        uint numberOfMediaAssets = 0;

        for(uint i = 0; i < mediaDatabase[_user].length;  i++) {
            mediaAssetIds[numberOfMediaAssets] = mediaDatabase[_user][i].id;
            numberOfMediaAssets++;
        }
        return mediaAssetIds;
    }

    function getNumberOfMediaForContributor(address _author) public view returns (uint) {
        return mediaDatabase[_author].length;
    }

    function getContributors() public view returns (address[]) {
        address[] memory mediaContributors = new address[](contributors.length);

        for(uint i = 0; i < contributors.length; i++) {
            mediaContributors[i] = contributors[i];
        }

        return mediaContributors;
    }
}
